INSERT INTO `jld_subject_select` VALUES ('1', '字词积累题', '单选题', '<p>下面注音全部正确的一项是（ ）&nbsp;</p><p>A．嗜好（hào） 破绽（dìng） 迸流（bèng）随声附和（hé） &nbsp; &nbsp;&nbsp;</p><p>B．惬意（xiá） 澎湃（bài） 赫然（hè）泰然处之（chǔ） &nbsp; &nbsp; &nbsp;&nbsp;</p><p>C．绮丽（qǐ） 贮藏（zhù） 枢纽（qū）茅塞顿开（sāi） &nbsp; &nbsp; &nbsp;&nbsp;</p><p>D．肇事（zhào） 歹徒（dǎi） 荡涤（dí）鲜为人道（xiǎn）</p><p><img src=\"/upload/image/20161015/1476524275562941.png\" title=\"1476524275562941.png\" alt=\"头像.png\" width=\"91\" height=\"93\"/></p>', '<p>D</p>', '4', '1', '1');
