INSERT INTO `jld_subject_select` VALUES ('24', '字词积累题', '单选题', '<p>下列注音全都正确的一项是（ ） &nbsp; &nbsp; &nbsp;&nbsp;</p><p>A．纠葛(gé) &nbsp; 债券(juàn) &nbsp; 亲昵(nì) &nbsp;侃侃(kǎn)而谈 &nbsp; &nbsp; &nbsp;&nbsp;</p><p>B．静谧(mì) &nbsp; 饶恕(shù) &nbsp; 蹉(cuō)跎 &nbsp;良莠(xiù)不齐 &nbsp; &nbsp; &nbsp;&nbsp;</p><p>C．炽(zhi)热 &nbsp; 栖(qi)息 &nbsp; 摩挲(suō) &nbsp;拈(zhān)轻怕重 &nbsp; &nbsp; &nbsp;&nbsp;</p><p>D．匀称(chèn) &nbsp;造诣(yì) &nbsp; 湮(yān)没 &nbsp;危言耸(sǒng)听 &nbsp;</p>', '<p>D</p>', '2', '1', '1');

-- ----------------------------
-- Table structure for jld_subject_subjective
-- ----------------------------
DROP TABLE IF EXISTS `jld_subject_subjective`;
CREATE TABLE `jld_subject_subjective` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(200) DEFAULT NULL COMMENT '题目要求',
  `type` varchar(100) NOT NULL COMMENT '题目类型（主观题下面的小分类）',
  `content` text NOT NULL COMMENT '题干',
  `result` text NOT NULL COMMENT '答案',
  `grade_id` int(5) DEFAULT NULL COMMENT '年级信息ID',
  `project_id` int(5) NOT NULL COMMENT '科目信息ID',
  `level` int(5) DEFAULT NULL COMMENT '难度星级1~5',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='主观题，一对一，比如作文和问答题';

-- ----------------------------
-- Records of jld_subject_subjective
-- ----------------------------
INSERT INTO `jld_subject_subjective` VALUES ('1', '按照要求写作文', '作文', '请写出一你做的一件有意义的事，不少于350字', '示例范文：\r\n久违的感觉\r\n一个星期没有打开电脑，打开作文库了，还真是有种久违的感觉。当然啦，作文和学业在我心里是一样重要的。看到自己的双手在键盘上跳动，灵活的思绪缠绕着整篇作文，每一篇作文虽然是随笔，可还是那么令人着迷。\r\n哪怕一个星期没有写过作文了，可是那些思绪那些提纲依旧在我脑海中浮现，双手放在键盘上，似乎只要手指一点点的跳跃，一篇文章就出来了。看着熟悉的界面，清晰的文字，原本萎靡的精神一下子振奋了！\r\n至少我的人生是有奋斗目标的，哪怕年纪再小，可是我也能赚钱了。我也能够写出很好的作文了，不需要老师再辛苦教诲一遍遍的替我改作文，不过学无止境也是我的目标。一篇作文也总是有值得修改的地方的，只有不断地改进，才能有更好的作品。没有最好，只有更好！\r\n话不多说，现在就开始写作文吧！开启一天的幸运！', '1', '1', '4');

-- ----------------------------
-- Table structure for jld_teacher
-- ----------------------------
DROP TABLE IF EXISTS `jld_teacher`;
CREATE TABLE `jld_teacher` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `tea_account` varchar(30) NOT NULL,
  `tea_name` varchar(30) NOT NULL,
  `tea_password` varchar(60) NOT NULL,
  `email` varchar(50) DEFAULT NULL,
  `login_date` varchar(20) DEFAULT NULL,
  `email_verify` tinyint(1) NOT NULL DEFAULT '0',
  `verify_code` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=28 DEFAULT CHARSET=utf8;

